import { react, useState } from 'react';
import '../styles/Form.css';


const Form = () => {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPass] = useState('');

    function PersistForm(){
        console.log(`UserName: ${name},  Email: ${email},  Password: ${password}`);
    }

    function ResetForm() {
        setName('');
        setEmail('');
        setPass('');
    } 

    return (
        <form>
            <label className='title'>
                <h3>Basic Form Functionality</h3>
                </label> <br />
            <label id='nameLabel' >Name</label>
            <input id='nameInput' required placeholder='Enter Name...' value={name} onChange={(nam) => setName(nam.target.value)}></input>
            <label id='emailLabel'></label>
            <input id='emailInput' type='email' required placeholder='Enter Email...' value={email} onChange={(ema) => setEmail(ema.target.value)}></input>
            <label id='passwordLabel'></label>
            <input id='passwordInput' type='password' required value={password} onChange={(pas) => setPass(pas.target.value)}></input>

            <input type='submit' onClick={PersistForm()}></input>
            {/*<input id='submit' type='submit' onSubmit={PersistForm()}></input>*/}
            {/*<input id='reset' type='reset' onClick={ResetForm()}></input>*/}
        </form>
        );
};

export default Form;
