import { react, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from '../Components/Form';
import { CopyBlock, dracula } from 'react-code-blocks';
import '../styles/Body.css';


const Body = () => {
    return (
        <div>
            <Container>
                <br/>
                <Row>
                    <Col className='titleCol'>
                        <h3 className='bodyTitle, Gold'>Purpose of React</h3>
                    </Col>
                    <hr className="Gold"/>
                    <li className="BiggerText">Build highly dynamic web applications in a efficient and reusable way using javascript. </li>
                    <li className="BiggerText">Makes the difficulty of creating dynamic user interfaces simplified by introducing statefull front end pages
                        while allowing for organized structure with components.</li>
                </Row>
                <Row className="sectionSpace">
                    <Col className="titleCol">
                        <h3 className="bodyTitle, Gold">Fundamentals Driving The Purpose of React</h3>
                    </Col>
                    <hr className="Gold" />
                    <Col xs={ 4 }>
                    <p className="BiggerText"> 1. <span className="important">Components:</span> Using components makes building UIs analogous to building with legos.</p>
                    <p className="BiggerText"> 2. UI is entirely written in javascript which aids in efficient UI updating when state is changed.</p>
                    <p className="BiggerText"> 3. <span className="important">Reconciliation:</span> Efficiency is maintained by updating only what is absolutely necessary. When UI
                        state changes only the changes are persisted to the DOM while everything else is left as is. (Partial DOM updating).</p>
                    </Col>
                </Row>

            </Container>
        </div>
        )
};

export default Body;
