import { react, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from '../Components/Form';
import { CopyBlock, dracula } from 'react-code-blocks';
import '../styles/Body.css';


const Body = () => {
    return (
        <div>
            <Container>
                <br/>
                <Row>
                    <Col className='titleCol'>
                        <h3 className='bodyTitle'>Purpose of React</h3>
                    </Col>
                    <hr/>
                    <li className="BiggerText">Build highly dynamic web applications in a efficient and reusable way using javascript. </li>
                    <li className="BiggerText">Makes the difficulty of creating dynamic user interfaces simplified by introducing statefull front end pages
                        while allowing for organized structure with components.</li>
                </Row>
                <Row className="sectionSpace">
                    <Col className="titleCol">
                        <h3 className="bodyTitle">Core Components Driving The Purpose of React</h3>
                    </Col>
                    <hr />
                    <label className="BiggerText">Components:</label>
                    <p className="BiggerText">using components makes building UIs analogous to building with legos</p>
                </Row>

            </Container>
        </div>
        )
};

export default Body;
