import { react, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from '../Components/Form';
import { CopyBlock, dracula } from 'react-code-blocks';
import '../styles/Body.css';


const Body = () => {
    return (
        <div>
            <Container>
                <br/>
                <Row>
                    <Col className='titleCol'>
                    <h3 className='bodyTitle3'>Purpose of react:</h3>
                    </Col>
                    <li>To build highly dynamic web applications in a efficient and reusable way using javascript. </li>
                    <li> Makes the difficulty of creating dynamic user interfaces simplified while allowing for manageable structure. </li>
                    <Col>

                    <p className='bodyText'>
                        To build highly dynamic web applications in a efficient and reusable way using javascript. 
                        </p>
                    </Col>
                    <br /><br /><br />
                    <p className='bodyText'> 
                        Makes the difficulty of creating dynamic user interfaces simplified while allowing for manageable structure.
                    </p>
                </Row>
            </Container>
        </div>
        )
};

export default Body;
